using AutoMapper;using Shop.BOL.Cervices.Common;using Shop.BOL.Models;using Shop.DAL.Entities;using Step.RepositoryInstatnt.Instatnt;namespace Shop.BOL.Cervices.Instatnt{	public class CategoryVMRep : CommonDTOService<CategoryVM, Category>	{		public CategoryVMRep(CategoryRep eRep) : base(eRep) { }		protected override MapperConfiguration MapConfig()		{			MapperConfiguration config = new MapperConfiguration(cfg =>			{				cfg.CreateMap<CategoryVM, Category>();				cfg.CreateMap<Category, CategoryVM>()				.ForMember("ParentName", opt => opt.MapFrom(ef => ef.Category2.CategoryName));			});			return config;		}	}}